mapper.select:
SELECT concat('firm_',#{firmCode}) AS id ,null AS parentId ,#{firmCode} AS firmCode ,(select name from firm where code=#{firmCode}) AS name ,null AS code ,null AS description ,null AS created_id ,null AS created ,null AS modified ,null AS modified_id union all
SELECT concat('department_',id) AS id ,case when parent_id is null then concat('firm_',firm_code) else concat('department_',parent_id) end as parentId , firm_code AS firmCode, name, code, description, created_id, created, modified, modified_id FROM department d WHERE 1=1
and d.firm_code = #{firmCode}
ORDER BY `${sort}`
${order}
