sql.where:
1 = 1

   and u.firm_code = #{firmCode}


  and u.department_id = #{departmentId}


  and u.state = #{state}


  and ur.role_id = #{roleId}


  and (u.user_name = #{keywords} or u.cellphone = #{keywords})

mapper.select:
SELECT d.id AS treeId, CASE WHEN d.parent_id ='-1' THEN CONCAT('firm_',d.firm_code) ELSE d.parent_id END AS parentId, d.`name`, CASE WHEN ud.id IS NULL THEN FALSE else true end AS checked FROM department d LEFT JOIN user_department ud ON d.id = ud.department_id and ud.user_id = #{userId} where 1 = 1
and d.firm_code = #{firmCode}
UNION ALL SELECT CONCAT('firm_',f.`code`) AS treeId, NULL AS parentId, f.`name`, FALSE AS checked FROM firm f where 1 = 1
and f.code = #{firmCode}
