sql.where:
			1 = 1

				and u.firm_code = #{firmCode}


				and u.department_id = #{departmentId}


				and u.state = #{state}


				and ur.role_id = #{roleId}


				and (u.user_name like concat('%',#{keywords},'%') or u.cellphone = #{keywords} or u.real_name like concat('%',#{keywords},'%'))


                and last_login_time >= #{lastLoginTimeStart}


                and last_login_time <= #{lastLoginTimeEnd}

mapper.select:
SELECT DISTINCT u.id, u.real_name, u.user_name, u.cellphone, u.email, u.department_id, d.`name` AS department_name, d.`code`, r.id AS role_id, r.role_name FROM `user` u LEFT JOIN department d ON u.department_id = d.id LEFT JOIN user_role ur ON ur.user_id = u.id LEFT JOIN role r ON r.id = ur.role_id WHERE u.firm_code="szpt"
AND u.id = #{userId}
AND u.real_name like concat('%',#{realName},'%')
AND u.department_id IN
#{deptId}
AND dr.role_id = #{roleId}

mapper.update:
UPDATE user
SET position_id = NULL
WHERE position_id = #{positionId,jdbcType=BIGINT}

UPDATE user
SET superior_id = NULL
WHERE superior_id = #{superiorId,jdbcType=BIGINT}

mapper.select:
SELECT u.department_id departmentId ,f.id firmId ,count(u.department_id) userCount FROM `user` u LEFT JOIN `firm` f ON u.firm_code = f.code WHERE u.created <= #{date} AND department_id IS NOT NULL
AND u.department_id IN
#{departmentId}
GROUP BY u.department_id;

select.foreach:
SELECT count(1) userCount,${map.seniorId} seniorId,f.id firmId FROM `user` u LEFT JOIN `firm` f ON u.firm_code = f.code
WHERE u.created <= #{map.date} AND f.code = #{map.firmCode} AND department_id IN

	#{departmentId}
