sql.where:
1 = 1

   and u.firm_code = #{firmCode}


  and u.department_id = #{departmentId}


  and u.state = #{state}


  and ur.role_id = #{roleId}


  and (u.user_name = #{keywords} or u.cellphone = #{keywords})

mapper.select:
SELECT d.id AS treeId, CASE WHEN d.parent_id is null THEN CONCAT('firm_',d.firm_code) ELSE d.parent_id END AS parentId, d.`name`, CASE WHEN ud.id IS NULL THEN FALSE ELSE TRUE END AS checked FROM department d LEFT JOIN user_data_auth ud ON d.id = ud.value and ud.user_id = #{userId} and ud.ref_code='department'
and d.firm_code = #{firmCode}
UNION ALL SELECT CONCAT('firm_',f.`code`) AS treeId, NULL AS parentId, f.`name`, CASE WHEN ud.id IS NULL THEN FALSE ELSE TRUE END AS checked FROM firm f LEFT JOIN user_data_auth ud ON f.code = ud.value and ud.user_id = #{userId} and ud.ref_code='market'
and f.code = #{firmCode}
